	`EncounterName12` text,
	`EncounterName13` text,
	`EncounterName14` text,
	`EncounterName15` text,
	`EncounterName16` text,
	`NameLangFlags` int(11) unsigned NOT NULL DEFAULT '0',
	`SpellIconID` int(11) unsigned NOT NULL DEFAULT '0',
